<?php$ajax=0;if(isset($_SERVER['HTTP_X_REQUESTED_WITH']) and $_SERVER['HTTP_X_REQUESTED_WITH'] == 'XMLHttpRequest')	$ajax=1;include "func.php";sec_session_start(['expire_in'=>'AII','mode'=>'ARK']);if(isset($_SESSION['id'])) {	$_SESSION = array();	if(session_destroy()) {		sec_session_start(['expire_in'=>0,'mode'=>'ARK']);		$_SESSION['msg']='green|You have successfully loged out.';		if(!$ajax)			header("location: index.php");		else			echo 'SORIX03';		exit;		}	}else {	$_SESSION=array();	session_destroy();	sec_session_start(['expire_in'=>0,'mode'=>'ARK']);	$_SESSION['msg']='info|You are not loged in.';	if($_SESS_PARAMS['fresh']) {		$_SESSION['msg']='info|Session Expired. You are not loged in.';	}	header("location: index.php");	exit;}?>